public class Solution {

    public ListNode FindKthToTail(ListNode head, int k) {
        // 注意点1：极端输入，直接输出结果
        if (head == null) {
            return null;
        }
        ListNode dummyNode = new ListNode(-1);
        dummyNode.next = head;
        ListNode fast = dummyNode;
        for (int i = 0; i < k; i++) {
